mapper.update:
        update cart c
        -- set
        
            
                product_skus_number = #{productSkusNumber,jdbcType=VARCHAR}
            
            
                checked = #{checked,jdbcType=INTEGER}
            
        

        where user_id = #{userId,jdbcType=BIGINT}
        and product_skus_id = #{productSkusId,jdbcType=BIGINT}
        and c.deleted != 1
