mapper.select:
SELECT pse.id, pse.additional_evaluation, pse.order_product_id, pse.product_skus_evaluation, pse.created_at, op.product_skus_id, op.order_sn, op.number, op.product_title, op.product_skus_title FROM product_skus_evaluation pse INNER JOIN order_product op ON op.id = pse.id WHERE pse.deleted !=1
and op.order_sn = #{orderSn,jdbcType=VARCHAR}
and op.order_product_id = #{orderProductId,jdbcType=VARCHAR}
and ps.product_skus_id = #{productSkusId}
ORDER BY pse.created_at desc

SELECT pse.additional_evaluation, pse.id, pse.order_product_id, pse.product_skus_evaluation, pse.created_at, op.product_skus_id, op.number, op.order_sn, op.product_title, op.product_skus_title FROM product_skus_evaluation pse INNER JOIN order_product op ON op.id = pse.id WHERE pse.deleted !=1
and op.order_sn = #{orderSn,jdbcType=VARCHAR}
and op.order_product_id = #{orderProductId,jdbcType=VARCHAR}
and ps.product_skus_id = #{productSkusId}
ORDER BY pse.created_at desc

mapper.update:
update product_skus_evaluation
set additional_evaluation = #{additionalEvaluation,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
