mapper.select:
SELECT ps.id, ps.product_id, ps.title, ps.avatar, ps.description, ps.stock, ps.created_at, p.title AS product_name FROM product as p INNER JOIN product_skus as ps ON ps.product_id = p.id WHERE ps.deleted != '1' AND p.deleted != '1'
and p.title like concat ('%',#{title,jdbcType=VARCHAR},'%')
and ps.title = #{productSkusName}
and ps.id = #{id}
and ps.product_id = #{productId}
ORDER BY ps.stock

mapper.update:
update product_skus

    
        product_id = #{productId,jdbcType=VARCHAR}
    
    
        title = #{title,jdbcType=INTEGER}
    
    
        avatar = #{avatar,jdbcType=VARCHAR}
    
    
        description = #{description,jdbcType=INTEGER}
    
    
        stock = #{stock,jdbcType=INTEGER}
    

where id = #{id,jdbcType=BIGINT}
